  this_block.addSimulinkInport('llr_b1');
  this_block.addSimulinkInport('llr_b0');
  this_block.addSimulinkInport('vin');
  this_block.addSimulinkInport('nrst');
  this_block.addSimulinkInport('packet_start');
  this_block.addSimulinkInport('packet_end');
  this_block.addSimulinkInport('early_trace');

  this_block.addSimulinkOutport('done');
  this_block.addSimulinkOutport('vout');
  this_block.addSimulinkOutport('dout_in_byte');

  done_port = this_block.port('done');
  done_port.setType('UFix_1_0');
  done_port.useHDLVector(false);
  vout_port = this_block.port('vout');
  vout_port.setType('UFix_1_0');
  vout_port.useHDLVector(false);
  dout_in_byte_port = this_block.port('dout_in_byte');
  dout_in_byte_port.setType('UFix_8_0');

  % -----------------------------
  if (this_block.inputTypesKnown)
    % do input type checking, dynamic output type and generic setup in this code block.

    if (this_block.port('nrst').width ~= 1);
      this_block.setError('Input data type for port "nrst" must have width=1.');
    end

    this_block.port('nrst').useHDLVector(false);

    if (this_block.port('packet_start').width ~= 1);
